import React from 'react';
import s from './Options.module.css';

const Options = (props) => {

  let index = props.state.number - 1;

  const addResponse = (e) => {
      let textAnswer = e.target.value;
      let newAnswer = {
          id: props.state.number,
          text: textAnswer
      }
      props.versionAnswer(newAnswer);
  }
  return (
    <ul className={s.answers}>
      <li>
          <input id="quest0" name="question" type="radio"
            onChange={addResponse} value={props.state.questions.item[index].var[0]} />
          <label htmlFor="quest0">{props.state.questions.item[index].var[0]}</label>
      </li>
      <li>
          <input id="quest1" name="question" type="radio"
            onChange={addResponse} value={props.state.questions.item[index].var[1]}/>
          <label htmlFor="quest1">{props.state.questions.item[index].var[1]}</label>
      </li>
      <li>
          <input id="quest2" name="question" type="radio"
            onChange={addResponse} value={props.state.questions.item[index].var[2]}/>
          <label htmlFor="quest2">{props.state.questions.item[index].var[2]}</label>
      </li>
      <li>
          <input id="quest3" name="question" type="radio"
            onChange={addResponse} value={props.state.questions.item[index].var[2]}/>
          <label htmlFor="quest3">{props.state.questions.item[index].var[3]}</label>
      </li>
    </ul>
    )
}

export default Options;
